function onSubmit() {
   //Type appropriate comment here, and begin script below
   var vehicleNumber = g_form.getValue("vehicle_no");

		if(vehicleNumber != '' ){
			var saveAndClose   = /^[A-Za-z]{2}[ -][0-9]{1,2}(?: [A-Za-z])?(?: [A-Za-z]{1,3})? [0-9]{4}$/g.test(vehicleNumber);
			if(!saveAndClose){
			alert('Invalid Vehicle Number....');
			g_form.clearValue('vehicle_no');
			return false;
			//annotation_vehicleNo.parentNode.style.display = "block";
			}
			else{
				return true;
			}
		}
		else{
			return true;
			//annotation_vehicleNo.parentNode.style.display = "none";
		}
	var visitor = g_form.getDisplayBox('visitor_fk').value;
	var employee = g_form.getDisplayBox('from_employee_fk').value;
	var redirectToEmployee = g_form.getValue('to_employee_fk');
	if(redirectToEmployee!=''){
		confirm("You want to send ");// + visitor + ", to "+ employee +"?");
	}

}
