sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var s=(new GlideDateTime()).getLocalTime().getByFormat('hh:mm:ss a');

	var glideRecordVisitor = new GlideRecord('x_373005_gatepass_visitor');

	//glideRecordGatepass.get(previous.sys_id);
	glideRecordVisitor.get(current.visitor_fk);

	var GatePassStatusValue=current.gatepass_status;
	if(GatePassStatusValue=="enter" && current.visit_enter_time==''){
		current.visit_enter_time=s;
		glideRecordVisitor.status='active';
	}
	else if(GatePassStatusValue=="in" && current.visit_in_time==''){
		current.visit_in_time=s;
	}
	else if(GatePassStatusValue=="out" && current.visit_out_time==''){
		if(current.visit_in_time==''){
			current.visit_in_time=s;
		}
		current.visit_out_time=s;
	}
	else if(GatePassStatusValue=="exit" && current.visit_exit_time==''){
		if(current.from_employee_fk!='' ){
			var glideRecordGatepass=new GlideRecord('x_373005_gatepass_gate_pass');
			glideRecordGatepass.addQuery('visit_out_time',current.visit_enter_time);
			glideRecordGatepass.addQuery('to_employee_fk',current.visitee_employee_fk);
			glideRecordGatepass.query();

			if(glideRecordGatepass.next()) {
				glideRecordGatepass.visit_exit_time=s;
				glideRecordGatepass.update();
			}
		}
		if(current.visit_in_time==''){
			current.visit_in_time=s;
		}
		if(current.visit_out_time==''){
			current.visit_out_time=s;
		}
		current.visit_exit_time=s;
		glideRecordVisitor.status='inactive';
	}
	current.update();
	glideRecordVisitor.update();

	if(current.to_employee_fk!='' && current.visit_exit_time==''){


 		var createNewGatepass=new createAndUpdateGatepass();
 		createNewGatepass.createGatepass(current.to_employee_fk, current.visitor_fk, current.reason_to_redirect, current.assets, current.vehicle_no, current.group_nos, current.visitee_employee_fk);

	}
})(current, previous);
