sys_script_include.script:
var createAndUpdateGatepass = Class.create();
createAndUpdateGatepass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	createGatepass:function(employee,visitor,reason,assets,vehicle_no,no_of_group_members,from_employee){

		var gr = new GlideRecord('x_373005_gatepass_gate_pass');
		gr.initialize();
		gr.	visitee_employee_fk = employee;
		gr.visitor_fk=visitor;
		gr.reason=reason;
		gr.gatepass_status='enter';
		gr.assets=assets;
		gr.vehicle_no=vehicle_no;
		gr.group_nos=no_of_group_members;
		gr.from_employee_fk= from_employee;
		gr.insert();
	},

	type: 'createAndUpdateGatepass'
});
