sys_script_include.script:
var Validations = Class.create();
Validations.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	isEmailValid:function(){

		var email = this.getParameter('sysparm_email');
		var str = email;
		var n = str.indexOf("@");
		var n1 = str.indexOf(".");
		var n2 = str.indexOf(".com");
		if (n == -1 || n1 == -1 || n2== -1) {
			return false;
		}
		else{
			return true;
		}
	},

	isMobileNumberValid:function(){
		var phoneno = /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/;
			phoneno=/^(?:(?:\+|0{0,2})91(\s*[\-]\s*)?|[0]?)?[6-9]\d{9}$/g;
				var mobileNo = this.getParameter('sysparm_mobileNo');
				var digits = mobileNo.length;
				if(mobileNo.length>14){
					gs.info('mob. no. is invalid mobileNo.length>14');
					return false;
				}
				else if (mobileNo.match(phoneno))
					{
					var code = '';
					var area = mobileNo.substring(0, 3);
					var prefix = mobileNo.substring(3, 6);
					var line = mobileNo.substring(6);
					gs.info('mob. no. is valid');
					return true;

				}
				else
					{
					gs.info('mob. no. is invalid !mobileNo.matchphoneno');
					return false;
				}
			},

	isNameValid:function(){
		var string = this.getParameter('sysparm_string');
		var characterValidaion   = /^[A-Za-z\. \s]+$/g.test(string);
	if(!characterValidaion){
		return false;
		//annotation_name.parentNode.style.display = "block";
	}
	else{
		return true;
		//annotation_name.parentNode.style.display = "none";
	}
	},

	isVehicleNumberValid:function(){
		var vehicleNumber = this.getParameter('sysparm_vehicle_no');
		var saveAndClose   = /^[A-Za-z]{2}[ -][0-9]{1,2}(?: [A-Za-z])?(?: [A-Za-z]{1,3})? [0-9]{4}$/g.test(vehicleNumber);

		if(!saveAndClose){
			return false;
			//annotation_vehicleNo.parentNode.style.display = "block";

		}
		else{
			return true;
			//annotation_vehicleNo.parentNode.style.display = "none";
		}

	},

	isPhoneNumberValid:function(){
		var phoneNumber = this.getParameter('sysparm_phone_number');
		var pattern = /[\d]{5}-[\d]{6}/g;

		if(!pattern.test(phoneNumber)){
			return false;
			//annotation_phoneNo.parentNode.style.display = "block";

		}
	else{
		return true;
		//annotation_phoneNo.parentNode.style.display = "none";
	}
	},

			type: 'Validations'
		});
